<node CREATED="1372744399121" FOLDED="true" ID="ID_1561802121" MODIFIED="1379999498533" TEXT="&#xb6; 10">
<node CREATED="1372744410183" ID="ID_1552231648" LINK="#ID_617766412" MODIFIED="1372746851410" TEXT="These directives ">
<node CREATED="1372746835769" ID="ID_1658735680" LINK="#ID_347375528" MODIFIED="1372746861242" TEXT="may appear anywhere comments are permitted (see section 2.2) "/>
<node CREATED="1372746841598" ID="ID_858784897" MODIFIED="1372746841600" TEXT="but must be followed by a delimiter. "/>
</node>
<node CREATED="1372746793626" ID="ID_1047916269" MODIFIED="1372746874734" TEXT="They are treated as comments, ">
<node CREATED="1372746874734" ID="ID_228231853" MODIFIED="1372746874736" TEXT="except that they affect the reading of subsequent data from the same port. "/>
</node>
