<node CREATED="1379745765524" FOLDED="true" ID="ID_592697647" MODIFIED="1379746280075" TEXT="&#xb6;&#xa0;3">
<node CREATED="1372828764835" ID="ID_1955624361" MODIFIED="1379745771193" TEXT="Every location is marked to show whether it is in use. "/>
<node CREATED="1372908681602" ID="ID_978048787" MODIFIED="1379745773897" TEXT="No variable or object ever refers to a location that is not in use. "/>
</node>
<node CREATED="1372828714662" FOLDED="true" ID="ID_1075330036" MODIFIED="1379746295035" TEXT="&#xb6; 4">
<node CREATED="1372908686300" ID="ID_407695852" MODIFIED="1379745806525" TEXT="Whenever this report speaks of storage being newly allocated for ">
<node CREATED="1372909388263" ID="ID_1016910902" MODIFIED="1372909392117" TEXT="">
<node CREATED="1372908706885" ID="ID_475704392" MODIFIED="1372908716448" TEXT="a variable "/>
<node CREATED="1372908716448" ID="ID_540100749" MODIFIED="1372908721842" TEXT="or object, "/>
</node>
<node CREATED="1372908721842" ID="ID_1533698743" MODIFIED="1372908775148" TEXT="what is meant is that ">
<node CREATED="1372908775149" ID="ID_1657229746" MODIFIED="1372908784060" TEXT="an appropriate number of locations are chosen from the set of locations that are not in use, "/>
<node CREATED="1372908784061" ID="ID_686653742" MODIFIED="1372908784062" TEXT="and the chosen locations are marked to indicate that they are now in use before the variable or object is made to denote them."/>
</node>
</node>
<node CREATED="1379295335503" ID="ID_719278642" MODIFIED="1379295335503" TEXT="">
<node CREATED="1379295340953" ID="ID_56842302" MODIFIED="1379295356518" TEXT="Notwithstanding this, it is understood that the empty list cannot be newly allocated, because it is a unique object. "/>
<node CREATED="1379295356560" ID="ID_1575039355" MODIFIED="1379295356562" TEXT="It is also understood that empty strings, empty vectors, and empty bytevectors, which contain no locations, may or may not be newly allocated."/>
</node>
</node>
<node CREATED="1372828718102" FOLDED="true" ID="ID_1183368481" MODIFIED="1379745953153" TEXT="&#xb6; 5">
<node CREATED="1372828772003" ID="ID_1136719937" MODIFIED="1372909067457" TEXT="Every object that denotes locations is either ">
<node CREATED="1372909067458" ID="ID_1698182449" MODIFIED="1372909076351" TEXT="mutable "/>
<node CREATED="1372909076351" ID="ID_521375342" MODIFIED="1372909076353" TEXT="or immutable. "/>
</node>
<node CREATED="1372908812703" ID="ID_960882529" MODIFIED="1372909116331">
<richcontent TYPE="NODE"><html>
  <head>
    
  </head>
  <body>
    <p>
      Literal constants,
    </p>
  </body>
</html></richcontent>
