<node CREATED="1372909456004" FOLDED="true" ID="ID_1006448329" MODIFIED="1379746205515" TEXT="&#xb6; 5">
<node CREATED="1372972659764" ID="ID_909336247" MODIFIED="1372972676536" TEXT="&#x2022;">
<node CREATED="1372910187645" ID="ID_551269085" MODIFIED="1372972672391" TEXT="If one of the following expressions is in a tail context, then the subexpressions shown as &#x27e8;tail expression&#x27e9; are in a tail context. "/>
<node CREATED="1372972637467" ID="ID_1739116702" LINK="#ID_773044546" MODIFIED="1372995159797" TEXT="These were derived from rules in the grammar given in chapter 7 by replacing ">
<node CREATED="1372995029484" ID="ID_1706028871" MODIFIED="1372995036910" TEXT="some occurrences of &#x27e8;body&#x27e9; with &#x27e8;tail body&#x27e9;, "/>
<node CREATED="1372995036910" ID="ID_200517767" MODIFIED="1372995044504" TEXT="some occurrences of &#x27e8;expression&#x27e9; with &#x27e8;tail expression&#x27e9;, "/>
<node CREATED="1372995044505" ID="ID_1329122486" MODIFIED="1372995044507" TEXT="and some occurrences of &#x27e8;sequence&#x27e9; with &#x27e8;tail sequence&#x27e9;. "/>
</node>
<node CREATED="1372972652784" ID="ID_968471487" MODIFIED="1372972652786" TEXT="Only those rules that contain tail contexts are shown here."/>
</node>
