    <p>
      (let loop ((numbers &#x2019;(3 -2 1 6 -5))
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(nonneg &#x2019;())
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(neg &#x2019;()))
    </p>
    <p>
      &#xa0;&#xa0;(cond ((null? numbers) (list nonneg neg))
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;((&gt;= (car numbers) 0)
    </p>
    <p>
