<node CREATED="1375053046830" ID="ID_409725680" MODIFIED="1375053048040" TEXT="&#xb6; 3">
<node CREATED="1375053048041" ID="ID_981727533" MODIFIED="1375053468106" TEXT="Import ">
<node CREATED="1375053504793" ID="ID_6927206" MODIFIED="1375053506059" TEXT="">
<node CREATED="1375053468107" ID="ID_555363248" MODIFIED="1375053472545" TEXT="declarations "/>
<node CREATED="1375053472546" ID="ID_1257769017" MODIFIED="1375053476786" TEXT="and definitions "/>
</node>
<node CREATED="1375053476786" ID="ID_1836656381" MODIFIED="1375053490167" TEXT="cause ">
<node CREATED="1375053490167" ID="ID_1170896605" MODIFIED="1375053497342" TEXT="bindings to be created in the global environment "/>
<node CREATED="1375053497343" ID="ID_1458386941" MODIFIED="1375053497344" TEXT="or modify the value of existing global bindings. "/>
</node>
</node>
<node CREATED="1375053442696" ID="ID_680970973" MODIFIED="1375053453381" TEXT="The initial environment of a program is empty, ">
<node CREATED="1375053453382" ID="ID_1094750482" MODIFIED="1375053453383" TEXT="so at least one import declaration is needed to introduce initial bindings."/>
</node>
</node>
<node CREATED="1375053052293" ID="ID_1747904835" MODIFIED="1375053053931" TEXT="&#xb6;&#xa0;4">
<node CREATED="1375053054309" ID="ID_445844225" MODIFIED="1375053519802" TEXT="Expressions occurring at the outermost level of a program do not create any bindings. "/>
<node CREATED="1375053519802" ID="ID_1571952910" MODIFIED="1375053542818" TEXT="They are ">
<node CREATED="1375053542819" ID="ID_1361055273" MODIFIED="1375053551589" TEXT="executed in order when the program is ">
<node CREATED="1375053551590" ID="ID_1376781021" MODIFIED="1375053569972" TEXT="invoked "/>
<node CREATED="1375053569973" ID="ID_193627587" MODIFIED="1375053569974" TEXT="or loaded, "/>
</node>
<node CREATED="1375053561115" ID="ID_871408438" MODIFIED="1375053561116" TEXT="and typically perform some kind of initialization."/>
</node>
</node>
<node CREATED="1375053062252" ID="ID_976630934" MODIFIED="1375053063842" TEXT="&#xb6;&#xa0;5">
<node CREATED="1375053064092" ID="ID_107470100" MODIFIED="1375053598507" TEXT="Programs and libraries are typically stored in files, although in some implementations they can be entered interactively into a running Scheme system. "/>
<node CREATED="1375053598509" ID="ID_1353994409" MODIFIED="1375053602543" TEXT="Other paradigms are possible. "/>
<node CREATED="1375053602543" ID="ID_283026586" MODIFIED="1375053617151" TEXT="Implementations which store libraries in files should ">
<node CREATED="1375053617151" ID="ID_237486841" MODIFIED="1375053623557" TEXT="document the mapping from the name of a library "/>
<node CREATED="1375053623558" ID="ID_631123447" MODIFIED="1375053623560" TEXT="to its location in the file system."/>
</node>
</node>
</node>
<node CREATED="1371765757556" FOLDED="true" ID="ID_1636429438" MODIFIED="1377916094038" TEXT="5.2 Import declarations">
<node CREATED="1375053648630" ID="ID_482840630" MODIFIED="1375053651869" TEXT="&#xb6; 1">
<node CREATED="1375053653072" ID="ID_1353286856" MODIFIED="1375053658267" TEXT="An import declaration takes the following form:"/>
<node CREATED="1375053662815" ID="ID_1710610667" MODIFIED="1375053678529">
<richcontent TYPE="NODE"><html>
  <head>
    
  </head>
  <body>
    <p>
      <font face="Courier">(import </font>&#x27e8;import-set&#x27e9; ...<font face="Courier">)</font>
    </p>
  </body>
</html></richcontent>
</node>
</node>
<node CREATED="1375053745809" ID="ID_1912348745" MODIFIED="1375053751783" TEXT="&#xb6; 2">
<node CREATED="1375053694916" ID="ID_478491889" MODIFIED="1375053799004" TEXT="An import declaration provides a way to import identifiers exported by a library. "/>
<node CREATED="1375053799004" ID="ID_1626243305" MODIFIED="1375053811655" TEXT="Each &#x27e8;import set&#x27e9; ">
<node CREATED="1375053811656" ID="ID_600174853" MODIFIED="1375053822069" TEXT="names a set of bindings from a library "/>
<node CREATED="1375053822069" ID="ID_46005232" MODIFIED="1375053822071" TEXT="and possibly specifies local names for the imported bindings. "/>
</node>
<node CREATED="1375053804805" ID="ID_1424768960" MODIFIED="1375053804806" TEXT="It takes one of the following forms:"/>
</node>
<node CREATED="1375053689213" ID="ID_31808379" MODIFIED="1375053754813" TEXT="&#xb6;&#xa0;3">
<node CREATED="1375053696908" ID="ID_109399121" MODIFIED="1375053701393" TEXT="&#x2022;">
<node CREATED="1375053709019" ID="ID_1831110964" MODIFIED="1375053709575" TEXT="&#x27e8;library name&#x27e9;"/>
</node>
<node CREATED="1375053719051" ID="ID_108181134" MODIFIED="1375053724503" TEXT="&#x2022;">
<node CREATED="1375053737321" ID="ID_764168591" MODIFIED="1375053871009">
<richcontent TYPE="NODE"><html>
