      <font face="Courier">(define (</font>&#x27e8;variable&#x27e9; &#x27e8;formals&#x27e9;<font face="Courier">) </font>&#x27e8;body&#x27e9;<font face="Courier">)</font>
    </p>
  </body>
</html></richcontent>
</node>
<node CREATED="1375054548526" ID="ID_1691021280" MODIFIED="1375054841066" TEXT="&#x27e8;Formals&#x27e9; are ">
<node CREATED="1375054854874" ID="ID_1193419183" MODIFIED="1375054856674" TEXT="">
<node CREATED="1375054841066" ID="ID_896181472" MODIFIED="1375054846142" TEXT="either a sequence of zero or more variables, "/>
<node CREATED="1375054846142" ID="ID_1821279368" MODIFIED="1375054852885" TEXT="or a sequence of one or more variables "/>
</node>
<node CREATED="1375054852885" ID="ID_1326261111" MODIFIED="1375054868242" TEXT="followed by ">
<node CREATED="1375054868242" ID="ID_603984446" MODIFIED="1375054882615" TEXT="a space-delimited period "/>
<node CREATED="1375054882616" ID="ID_177046379" MODIFIED="1375054882617" TEXT="and another variable (as in a lambda expression). "/>
</node>
</node>
<node CREATED="1375054815801" ID="ID_1838886514" MODIFIED="1375054815803" TEXT="This form is equivalent to">
