<node CREATED="1371765785382" FOLDED="true" ID="ID_1044079234" MODIFIED="1375157098875" TEXT="5.5 Record-type definitions">
<node CREATED="1375073671370" ID="ID_1249594358" MODIFIED="1375073673032" TEXT="&#xb6;&#xa0;1">
<node CREATED="1375073678044" ID="ID_346818761" MODIFIED="1375083694671" TEXT="Record-type definitions are used to introduce new data types, called record types. "/>
<node CREATED="1375083694671" ID="ID_1497798652" MODIFIED="1375084621587" TEXT="Like other definitions, they can appear either at ">
<node CREATED="1375084621587" ID="ID_1441533764" MODIFIED="1375084627848" TEXT="the outermost level "/>
<node CREATED="1375084627849" ID="ID_1926201940" MODIFIED="1375084627851" TEXT="or in a body. "/>
</node>
<node CREATED="1375083702805" ID="ID_1337170230" MODIFIED="1375084639685" TEXT="The values of a record type are called records ">
<node CREATED="1375084639686" ID="ID_365575990" MODIFIED="1375084649455" TEXT="and are aggregations of zero or more fields, "/>
<node CREATED="1375084649455" ID="ID_334493294" MODIFIED="1375084649457" TEXT="each of which holds a single location. "/>
</node>
<node CREATED="1375083710335" ID="ID_299720238" MODIFIED="1375084702774" TEXT="">
<node CREATED="1375084708028" ID="ID_1994992849" MODIFIED="1375084710691" TEXT="">
<node CREATED="1375084664495" ID="ID_1377970730" MODIFIED="1375084695707" TEXT="A predicate, "/>
<node CREATED="1375084671898" ID="ID_264375608" MODIFIED="1375084676246" TEXT="a constructor, "/>
<node CREATED="1375084676246" ID="ID_144109153" MODIFIED="1375084689834" TEXT="and field accessors and mutators"/>
</node>
<node CREATED="1375084689834" ID="ID_1182721752" MODIFIED="1375084689837" TEXT=" are defined for each record type."/>
</node>
</node>
<node CREATED="1375073684819" ID="ID_954829822" MODIFIED="1375073687337" TEXT="syntax">
