<node CREATED="1377016579693" ID="ID_1133195879" MODIFIED="1377051194314" TEXT="     (define-syntax cond&#xa;       (syntax-rules (else =&gt;)&#xa;         ((cond (else result1 result2 ...))&#xa;          (begin result1 result2 ...))&#xa;         ((cond (test =&gt; result))&#xa;          (let ((temp test))&#xa;            (if temp (result temp))))&#xa;         ((cond (test =&gt; result) clause1 clause2 ...)&#xa;          (let ((temp test))&#xa;            (if temp&#xa;                (result temp)&#xa;                (cond clause1 clause2 ...))))&#xa;         ((cond (test)) test)&#xa;         ((cond (test) clause1 clause2 ...)&#xa;          (let ((temp test))&#xa;            (if temp &#xa;                temp&#xa;                (cond clause1 clause2 ...))))&#xa;         ((cond (test result1 result2 ...))&#xa;          (if test (begin result1 result2 ...)))&#xa;         ((cond (test result1 result2 ...)&#xa;                clause1 clause2 ...)&#xa;          (if test&#xa;              (begin result1 result2 ...)&#xa;              (cond clause1 clause2 ...)))))&#xa;">
<font NAME="Courier" SIZE="12"/>
</node>
