<node CREATED="1371765658566" FOLDED="true" ID="ID_638218027" MODIFIED="1379480044476" TEXT="A Standard Libraries">
<node CREATED="1377146071651" FOLDED="true" ID="ID_956294205" MODIFIED="1377240927992" TEXT="&#xb6; 1">
<node CREATED="1377146079636" ID="ID_1680773674" MODIFIED="1377154247206" TEXT="This section lists the exports provided by the standard libraries. "/>
<node CREATED="1377154247206" ID="ID_724893425" MODIFIED="1377154267249" TEXT="The libraries are factored so as to separate features which ">
<node CREATED="1377154267250" ID="ID_37191482" MODIFIED="1377154276927" TEXT="might not be supported by all implementations, "/>
<node CREATED="1377154276928" ID="ID_1070690744" MODIFIED="1377154276928" TEXT="or which might be expensive to load."/>
