<node CREATED="1377403665034" ID="ID_785728582" MODIFIED="1379485361908">
<richcontent TYPE="NODE"><html>
  <head>
    
  </head>
  <body>
    <p>
      (define (elementwise f)
    </p>
    <p>
      &#xa0;&#xa0;(lambda vectors
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;(generate-vector
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(vector-length (car vectors))
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(lambda (i)
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(apply f
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(map (lambda (v) (vector-ref&#xa0;&#xa0;v i))
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;vectors))))))
    </p>
  </body>
</html></richcontent>
