<node CREATED="1377403707520" ID="ID_1425252949" MODIFIED="1377403708964" TEXT="&#xb6; 7">
<node CREATED="1377403713910" ID="ID_1828982757" MODIFIED="1379485387994">
<richcontent TYPE="NODE"><html>
  <head>
    
  </head>
  <body>
    <p>
      (define (generate-vector size proc)
    </p>
    <p>
      &#xa0;&#xa0;(let ((ans (make-vector size)))
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;(letrec ((loop
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(lambda (i)
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(cond ((= i size) ans)
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(else
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(vector-set! ans i (proc i))
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(loop (+ i 1)))))))
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;(loop 0))))
    </p>
  </body>
</html></richcontent>
