<node CREATED="1377403824495" ID="ID_140431072" MODIFIED="1377403825910" TEXT="&#xb6; 11">
<node CREATED="1377403832519" ID="ID_207088210" MODIFIED="1379485444662">
<richcontent TYPE="NODE"><html>
  <head>
    
  </head>
  <body>
    <p>
      (define (map-streams f s)
    </p>
    <p>
      &#xa0;&#xa0;(cons (f (head s))
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(delay (map-streams f (tail s)))))
    </p>
  </body>
</html></richcontent>
