<node CREATED="1377404032697" ID="ID_743056101" MODIFIED="1377404033272" TEXT="The following illustrates the use of integrate-system in integrating the system"/>
<node CREATED="1377404038250" ID="ID_1844432314" MODIFIED="1377406400392" TEXT="C dv/dt = -i -v/R"/>
<node CREATED="1377404041521" ID="ID_1024464270" MODIFIED="1377404045960" TEXT="LdiL =vC dt"/>
<node CREATED="1377404221015" ID="ID_505607290" MODIFIED="1377404221787" TEXT="which models a damped oscillator."/>
</node>
<node CREATED="1377404313385" ID="ID_1415853012" MODIFIED="1377404315373" TEXT="&#xb6; 15">
<node CREATED="1377404326400" ID="ID_1698274507" MODIFIED="1379485481967">
<richcontent TYPE="NODE"><html>
  <head>
    
  </head>
  <body>
    <p>
      (define (damped-oscillator R L C)
    </p>
    <p>
      &#xa0;&#xa0;(lambda (state)
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;(let ((Vc (vector-ref state 0))
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(Il (vector-ref state 1)))
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(vector (- 0 (+ (/ Vc (* R C)) (/ Il C)))
    </p>
    <p>
      &#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;(/ Vc L)))))
    </p>
  </body>
</html></richcontent>
